import PropTypes from "prop-types";
import { Component } from "react";
import queryString from "query-string";
import Searchbar from "../Components/Searchbar";
import { fetchMoviesByQuery } from "../services/movieApi";
import { Box } from "@chakra-ui/react";
import { Link } from "react-router-dom";
import { ReactPaginate } from "react-paginate";

export default class MoviesPage extends Component {
  state = {
    movies: [],
    error: null,
    loading: false,
    data: [],
    offset: 0,
  };


  loadCommentsFromServer() {
    $.ajax({
      url: this.props.url,
      data: { limit: this.props.perPage, offset: this.state.offset },
      dataType: 'json',
      type: 'GET',

      success: (data) => {
        this.setState({
          data: data.comments,
          pageCount: Math.ceil(data.meta.total_count / data.meta.limit),
        });
      },

      error: (xhr, status, err) => {
        console.error(this.props.url, status, err.toString()); // eslint-disable-line
      },
    });
  }

  componentDidMount() {
    
  }
  componentDidMount() {
    const { querry } = queryString.parse(this.props.location.search);
    if (querry) {
      this.setState({ loading: true });
      fetchMoviesByQuery(querry)
        .then((res) => this.setState({ movies: res.data.results }))
        .catch((error) => this.setState({ error }))
        .finally(() => this.setState({ loading: false }));
    }
  }

  componentDidUpdate(prevProps) {
    const prevSearch = prevProps.location.search;
    const newSearch = this.props.location.search;

    if (prevSearch !== newSearch) {
      const { querry } = queryString.parse(newSearch);
      console.log(querry);
      this.setState({ loading: true });
      fetchMoviesByQuery(querry)
        .then((res) => this.setState({ movies: res.data.results }))
        .catch((error) => this.setState({ error }))
        .finally(() => this.setState({ loading: false }));
    }
  }

  handleSearch = (querry) => {
    this.props.history.push({
      ...this.props.location,
      search: `?querry=${querry}`,
    });
  };
  render() {
    const { movies, loading, error } = this.state;
    return (
      <div>
        <Searchbar onSearch={this.handleSearch} />
        {loading && <h2>Loading...</h2>}
        {error && <h2>Error, {error.message}</h2>}
        {movies.length > 0 && (
          <ul>
            {movies.map((movie) => (
              <li key={movie.id}>
                <Link to={`/movies/${movie.id}`}>{movie.title}</Link>
              </li>
            ))}
          </ul>
        )}
      </div>
    );
  }
}

MoviesPage.propTypes = {
  history: PropTypes.object,
  location: PropTypes.object,
};
