import { Link } from "react-router-dom";
import { fetchMovieDetails } from "../../services/movieApi";
import { Component } from "react";
import styles from "./MovieDetailsPage.module.css";
import Cast from "../Cast";
import Reviews from "../Reviews";

export default class MovieDetailsPage extends Component {
  state = {
    moviesDetails: null,
    error: null,
    loading: false,
  };

  componentDidMount() {
    this.setState({ loading: true, error: null });

    fetchMovieDetails(this.props.match.params.movieId)
      .then((res) => this.setState({ moviesDetails: res.data }))
      .catch((error) => this.setState({ error }))
      .finally(() => this.setState({ loading: false }));
  }

  render() {
    const { moviesDetails } = this.state;
    return (
      <div>
        {/* {console.log(this.state.moviesDetails)} */}
        {moviesDetails !== null && (
          <div>
            <Link to="/">Home</Link>
            <br></br>
            <Link to="/movies">Movies</Link>

            <br></br>
            <img
              width={640}
              src={`https://image.tmdb.org/t/p/original${moviesDetails.backdrop_path}`}
            />
            <h2>
              {this.state.moviesDetails.original_title}(
              {this.state.moviesDetails.release_date})
            </h2>
            <p>user score:{this.state.moviesDetails.vote_average * 10}%</p>
            <h2>Overview</h2>
            <p>{this.state.moviesDetails.overview}</p>
            <h2>Genres</h2>
            {this.state.moviesDetails.genres.map((obj) => (
              <p>{obj.name}</p>
            ))}

            <h2>Additional information</h2>

            <ul>
              <li className={styles.cast}>
                <p>
                  Cast <Cast movDetail={this.state.moviesDetails} />
                </p>
              </li>
              <li className={styles.reviews}>
                <p>
                Reviews <Reviews movDetail={this.state.moviesDetails}/>
                </p>
              </li>
            </ul>
            <div></div>
          </div>
        )}
      </div>
    );
  }
}
