import { Link } from "react-router-dom";
import { fetchMovieDetails } from "../services/movieApi";
import { Component } from "react";

export default class MovieDetailsPage extends Component {
  state = {
    moviesDetails: [],
    error: null,
    loading: false,

  };

  componentDidMount() {
    this.setState({ loading: true, error: null });

    fetchMovieDetails(this.props.match.params.movieId)
      .then((res) => this.setState({moviesDetails: res.data}))
      .catch((error) => this.setState({ error }))
      .finally(() => this.setState({ loading: false }));
  }

  render() {
   
    return (
      <div>
        <Link to="/">Home</Link>
        <br></br>
        <Link to="/movies">Movies</Link>
        {console.log(this.state.moviesDetails)}
        <img src="" />
        <h2>{this.state.moviesDetails.original_title}()</h2>
        <p>user score:{this.state.moviesDetails.vote_average*10}%</p>
        <h2>Overview</h2>
        <p>{this.state.moviesDetails.overview}</p>
        <h2>Genres</h2>
        <p>{this.state.moviesDetails.genres.map()}</p>
      </div>
    );
  }
}
