import React from "react";
import { Link, WrapItem, Image, Box, Wrap } from "@chakra-ui/react";
import { urls } from "../../router";
import { Link as LinkReact } from "react-router-dom";

export default function MovieList({ movies }) {
  return (
    <Wrap>
      {movies.map((movie) => (
        <WrapItem key={movie.id} d='flex' flexDirection='column'>
          <Link as={LinkReact} to={`${urls.movies}/${movie.id}`}>
            {movie.title}
          </Link>
          <Box boxSize={400}>
            <Image
              h="100%"
              src={`https://image.tmdb.org/t/p/w500${movie.poster_path}`}
            ></Image>
          </Box>
        </WrapItem>
      ))}
    </Wrap>
  );
}
