import { Link } from "react-router-dom";
import { fetchMovieDetails } from "../../services/movieApi";
import { Component } from "react";

import Cast from "../Cast";
import Reviews from "../Reviews";

export default class MovieDetailsPage extends Component {
  state = {
    moviesDetails: null,
    error: null,
    loading: false,
  };

  componentDidMount() {
    this.setState({ loading: true, error: null });

    fetchMovieDetails(this.props.match.params.movieId)
      .then((res) => this.setState({ moviesDetails: res.data }))
      .catch((error) => this.setState({ error }))
      .finally(() => this.setState({ loading: false }));
  }
  goBackFunc = () => {
    this.props.history.goBack();
  };

  render() {
    const { moviesDetails } = this.state;
    return (
      <div>
        {/* {console.log(this.state.moviesDetails)} */}
        {moviesDetails !== null && (
          <div>
            <Link to="/">Home</Link>
            <br></br>
            <Link to="/movies">Movies</Link>
            <br></br>
            <button onClick={this.goBackFunc}>Go back</button>
            <br></br>
            <img
              width={640}
              src={`https://image.tmdb.org/t/p/original${moviesDetails.backdrop_path}`}
            />
            <h2>
              {this.state.moviesDetails.original_title}(
              {this.state.moviesDetails.release_date})
            </h2>
            <p>user score:{this.state.moviesDetails.vote_average * 10}%</p>
            <h2>Overview</h2>
            <p>{this.state.moviesDetails.overview}</p>
            <h2>Genres</h2>
            {this.state.moviesDetails.genres.map((obj) => (
              <p>{obj.name}</p>
            ))}

            <h2>Additional information</h2>
       
            <ul>
              <li>
                <Link to={this.props.location.pathname.includes("cast") ?  `movies/${this.state.moviesDetails.id}`  : `movies/${this.state.moviesDetails.id}/cast`}>Cast</Link>
                {this.props.location.pathname.includes("cast") ? (
                  <Cast movDetail={this.state.moviesDetails} />
                ) : null}
              </li>
              <li>
                <Link  to={this.props.location.pathname.includes("reviews") ?  `movies/${this.state.moviesDetails.id}`  : `movies/${this.state.moviesDetails.id}/reviews`}>
                  Reviews
                </Link>
                {this.props.location.pathname.includes("reviews") ? (
                  <Reviews movDetail={this.state.moviesDetails} />
                ) : null}
              </li>
            </ul>
            <div></div>
          </div>
        )}
      </div>
    );
  }
}
